// src/components/StudentDetails.js
import React, { useState } from 'react';

function StudentDetails() {
  const [studentId, setStudentId] = useState('');
  const [exams, setExams] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleInputChange = (e) => {
    setStudentId(e.target.value);
  };

  const handleFetchExams = () => {
    if (!studentId) {
      setError('Please enter a student ID.');
      return;
    }

    setLoading(true);
    setError(null);
    setExams([]);

    fetch(`http://localhost:5131/api/Students/${studentId}/exams`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Student or exams not found.');
        }
        return response.json();
      })
      .then((data) => {
        setExams(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error:', error);
        setError(error);
        setLoading(false);
      });
  };

  return (
    <div>
      <h2>Student Details</h2>
      <div>
        <input
          type="text"
          placeholder="Enter Student ID"
          value={studentId}
          onChange={handleInputChange}
        />
        <button onClick={handleFetchExams}>Get Exams</button>
      </div>

      {loading && <div>Loading...</div>}

      {error && <div>Error: {error.message}</div>}

      {!loading && !error && exams.length > 0 && (
        <div>
          <h3>Exams for Student ID: {studentId}</h3>
          {exams.map((exam) => (
            <div key={exam.id}>
              <h4>Exam ID: {exam.id}</h4>
              <table>
                <thead>
                  <tr>
                    <th>Task ID</th>
                    <th>Task Text</th>
                    <th>Correct Result</th>
                    <th>Student Result</th>
                    <th>Is Correct</th>
                  </tr>
                </thead>
                <tbody>
                  {exam.tasks.map((task) => (
                    <tr key={task.id}>
                      <td>{task.id}</td>
                      <td>{task.taskText}</td>
                      <td>{task.correctResult}</td>
                      <td>{task.studentResult}</td>
                      <td>{task.isCorrect ? 'Yes' : 'No'}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))}
        </div>
      )}

      {!loading && !error && exams.length === 0 && studentId && (
        <div>No exams available for this student.</div>
      )}
    </div>
  );
}

export default StudentDetails;
