// src/components/TeacherUpload.js
import React, { useState } from 'react';

function TeacherUpload() {
  const [file, setFile] = useState(null);
  const [message, setMessage] = useState('');

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file) {
      setMessage('Please select a valid XML file.');
      return;
    }

    const formData = new FormData();
    formData.append('xmlFile', file);

    try {
      const response = await fetch('http://localhost:5131/api/Exams/upload', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        setMessage('File processed successfully.');
      } else {
        setMessage('An error occurred while processing the file.');
      }
    } catch (error) {
      console.error('Error:', error);
      setMessage('An error occurred while processing the file.');
    }
  };

  return (
    <div>
      <h2>Upload Exams</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="xmlFile">Select XML File</label>
          <input type="file" name="xmlFile" id="xmlFile" onChange={handleFileChange} />
        </div>
        <button type="submit">Upload</button>
      </form>
      {message && <div>{message}</div>}
    </div>
  );
}

export default TeacherUpload;
